mapper.select:
select
  id, record_time, spending_type, amount_paid, remark, create_time, create_user_id, update_time, update_user_id
from billing_details


        and id = #{id}


        and record_time = #{recordTime}


        and spending_type = #{spendingType}


        and amount_paid = #{amountPaid}


        and remark = #{remark}


        and create_time = #{createTime}


        and create_user_id = #{createUserId}


        and update_time = #{updateTime}


        and update_user_id = #{updateUserId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from billing_details


        and id = #{id}


        and record_time = #{recordTime}


        and spending_type = #{spendingType}


        and amount_paid = #{amountPaid}


        and remark = #{remark}


        and create_time = #{createTime}


        and create_user_id = #{createUserId}


        and update_time = #{updateTime}


        and update_user_id = #{updateUserId}
